<div class="form-group" ng-class="{'has-success': form['{{name}}'].$valid && form['{{name}}'].$dirty && !form['{{name}}'].$error.required, 'has-error': form['{{name}}'].$dirty && form['{{name}}'].$invalid && !form['{{name}}'].$error.required, 'has-warning' : form['{{name}}'].$dirty && form['{{name}}'].$invalid && form['{{name}}'].$error.required}">
    <label for="{{id}}" class="control-label" ng-bind="label ? label : '{{name}}'" ng-class="{'sr-only' : !label}"></label>
    <textarea msd-elastic rows="{{rows}}" name="{{name}}" id="{{id}}" placeholder="{{placeholder}}" ng-minlength="{{ngMinlength}}" ng-maxlength="{{ngMaxlength}}" ng-model="ngModel" ng-model-options="{allowInvalid : true}" ng-required="required ? true : false" ng-readonly="readonly ? true : false" uib-popover-template="popover.templateUrl" popover-placement="bottom" popover-trigger="none" popover-is-open="thereAreErrors" ng-focus="triggerIfDirtyAndInvalid()" ng-change="triggerIfDirtyAndInvalid()" ng-blur="closeValidationPopover()"></textarea>
</div>